mapper.select:
select
    stat_name,
    stat_val,
    stat_date
from
    t_station_data_stat
where
    1 = 1
    and station_id = #{stationId,jdbcType=BIGINT}
    and year = #{year,jdbcType=INTEGER}
    and month = #{month,jdbcType=INTEGER}

    and stat_type = #{statType,jdbcType=INTEGER}

order by stat_date

select stat_name, month, sum(stat_val) sumVal from t_station_data_stat where 1 = 1 and station_id = #{stationId,jdbcType=BIGINT}
and stat_type = #{statType,jdbcType=INTEGER}
and year = #{year,jdbcType=INTEGER} group by stat_name,year,month
